import React, { Component} from 'react';
import {BrowserRouter, Route, Switch} from 'react-router-dom'
import apiKey from './config'
import Nav from './components/Nav'
import PhotoContainer from './components/PhotoContainer'
import NotFound from './components/NotFound'
import SearchForm from './components/SearchForm'

class App extends Component {

  constructor() {
    super();
    this.state = {
      photos: [],
      dogPhotos: [],
      catPhotos: [],
      computerPhotos: []
    };
  }



  searchResults = (query) => {
    fetch( `https://www.flickr.com/services/rest/?method=flickr.photos.search&api_key=${apiKey}&tags=${query}&per_page=24&format=json&nojsoncallback=1`)
    .then(response => response.json())
    .then(responseData=> {
      this.setState({photo: responseData.photos.photo })
      })
    .catch(err=> {
      console.log('Error fetching and parsing data', err)
      })
  }



  dogPics = () => {
    fetch(`https://www.flickr.com/services/rest/?method=flickr.photos.search&api_key=${apiKey}&tags=dogs&per_page=24&format=json&nojsoncallback=1`)
    .then(response => response.json())
    .then(responseData=> {
      this.setState({ dogPhotos: responseData.photos.photo })
  })
    .catch(err=> console.log('Error fetching and parsing data', err))
  }


  catPics = () => {
      fetch(`https://www.flickr.com/services/rest/?method=flickr.photos.search&api_key=${apiKey}&tags=cats&per_page=24&format=json&nojsoncallback=1`)
      .then(response => response.json())
      .then(responseData=> {
        this.setState({ catPhotos: responseData.photos.photo })
})
  .catch(err=> console.log('Error fetching and parsing data', err))
}

  computerPics = () => {
    fetch(`https://www.flickr.com/services/rest/?method=flickr.photos.search&api_key=${apiKey}&tags=computers&per_page=24&format=json&nojsoncallback=1`)
    .then(response => response.json())
    .then(responseData=> {
      this.setState({ computerPhotos: responseData.photos.photo })
})
  .catch(err=> console.log('Error fetching and parsing data', err))

}

componentDidMount() {
  this.searchResults();
  this.dogPics();
  this.catPics();
  this.computerPics();

}


  render() {
      console.log(this.state.photos);
      console.log(this.state.dogPhotos)
    return (

    <BrowserRouter>
      <div className="container">
        <SearchForm onSearch={this.searchResults} />
        <Nav/>
        <Switch>
          <Route exact path= "/" render={ () => <PhotoContainer data={this.state.photos}/>} />
          <Route exact path= "/search/:query" render={ () => <PhotoContainer data={this.state.photos}/>} />
          <Route path= "/dogs" render={ () => <PhotoContainer data={this.dogPhotos}/>} />
          <Route path= "/cats" render={ () => <PhotoContainer data={this.catPhotos}/>} />
          <Route path= "/computers" render={ () => <PhotoContainer data={this.computerPhotos}/>} />
          <Route component={NotFound} />
      </Switch>
    </div>
  </BrowserRouter>
  );
}
}

export default App;
